var TenantsAPI = require('oae-tenants');

/**
 * The UserArchive model.
 *
 * @param  {String}     tenantAlias                 The alias of the tenant this user belongs to
 * @param  {String}     archiveId                   The globally unique id for this userArchive. e.g.: g:cam:math-101
 */
module.exports.UserArchive = function(tenantAlias, archiveId) {
    var that = {};
    that.tenantAlias = tenantAlias;
    that.archiveId = archiveId;
    return that;
};
