var OAE = require('oae-util/lib/oae');
var TransferAPI = require('oae-transfer');

/**
 * @REST postTransferCreate
 *
 * Create a new transfer
 *
 * @Server      tenant
 * @Method      POST
 * @Path        /transfer/create
 * @FormParam   {Context}           ctx                 Context
 * @FormParam   {string}            emailOrigin         The email
 * @FormParam   {string}            emailTarget         The new email       		
 * @FormParam   {string}            idUserOrigin        The ID user       		
 * @Return      {transfer}                              The created transfer
 * @HttpResponse                    200                 Transfer created
 * @HttpResponse                    400                 EmailTarget cannot be empty
 * @HttpResponse                    400                 Transfer not found
 */
OAE.tenantRouter.on('post', '/api/transfer/create', function (req, res) {
    TransferAPI.createTransfer(req.ctx, req.body.emailOrigin, req.body.emailTarget, req.body.idUserOrigin, function (err, transfer) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        return res.send(200, transfer);
    });
});

/**
 * @REST getTransferByEmail
 *
 * Get transfer
 *
 * @Server      tenant
 * @Method      GET
 * @Path        /transfer/getTransferByEmail/:email
 * @FormParam   {Context}           ctx                 Context
 * @FormParam   {string}            email               Email               
 * @Return      {transfer}                              The created transfer
 * @HttpResponse                    200                 Transfer created
 * @HttpResponse                    400                 Transfer not found
 */
OAE.tenantRouter.on('get', '/api/transfer/getTransferByEmail/:email', function (req, res) {
    TransferAPI.getTransferByEmail(req.ctx, req.params.email, function (err, transfer) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        return res.send(200, transfer);
    });
});

/**
 * @REST makeTransfer
 *
 * Make transfer
 *
 * @Server      tenant
 * @Method      POST
 * @Path        /transfer/makeTransfer
 * @FormParam   {Context}           ctx                 Context
 * @FormParam   {string}            emailOrigin         The email
 * @FormParam   {string}            code                Generated code
 * @FormParam   {string}            emailTarget         The new email               
 * @FormParam   {string}            idUserTarget        The ID user                           
 * @Return      {transfer}                              The created transfer
 * @HttpResponse                    200                 Transfer created
 * @HttpResponse                    400                 Email cannot be empty
 * @HttpResponse                    400                 Code cannot be empty
 * @HttpResponse                    400                 Invalid email
 * @HttpResponse                    400                 Transfer not found
 * @HttpResponse                    400                 There is no data to transfer
 * @HttpResponse                    400                 Code doesn't match
 * @HttpResponse                    400                 Transfer not found
 */
OAE.tenantRouter.on('post', '/api/transfer/makeTransfer', function (req, res) {
    TransferAPI.makeTransfer(req.ctx, req.body.emailOrigin, req.body.code, req.body.emailTarget, req.body.idUserTarget, function (err, managers) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        return res.send(200, managers);
    });

});

/**
 * @REST deleteTransfer
 *
 * Delete transfer
 *
 * @Server      tenant
 * @Method      POST
 * @Path        /transfer/deleteTransfer
 * @FormParam   {Context}           ctx                 Context
 * @FormParam   {string}            emailOrigin         The email                         
 * @Return      {transfer}                              The created transfer
 * @HttpResponse                    200                 Transfer created
 * @HttpResponse                    400                 Email cannot be empty
 * @HttpResponse                    400                 Invalid email
 */
OAE.tenantRouter.on('post', '/api/transfer/deleteTransfer', function (req, res) {
    TransferAPI.deleteTransfer(req.ctx, req.body.emailOrigin, function (err) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        return res.send(200);
    });

});
